package main

import (
	"fmt"
	"os"
)

func checkError(err error) {
	if err != nil {
		panic(err)
	}
}

func main() {

	// as per source code The filename is generated by taking pattern and adding a random string to the end.
	tempFile, err := os.CreateTemp("", "somefile")

	checkError(err)
	fmt.Println("temp file created", tempFile)

	defer tempFile.Close()
	// dont forget to remove temporary files
	defer os.Remove(tempFile.Name())

	// just like tempfile addes random string at the end
	tempDir, err := os.MkdirTemp("", "GocourTempDir")
	checkError(err)

	//defer os.Remove(tempDir)
	defer os.RemoveAll(tempDir)
	fmt.Println("Tempdoreary directoty ", tempDir)
}
